import React, {Component} from 'react';
import './contact.css';
import Notification from '../thankyoupage/Notification';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';

// const textAreaDesign = {   width: '100%',   minHeight: '12px',   marginTop:
// '30px',   marginBottom: '60px',   border: 'none',   borderBottom:'solid 0.2px
// #9055fb',   fontSize: '18px',   fontColor: '#585c61' }

class Contact extends Component {
    constructor() {
        super();
        this.state = {
            name: '',
            email: '',
            message: ''
        }
    }

    handleName = (e) => {
        this.setState({name: e.currentTarget.value});
    }

    handleEmail = (e) => {
        this.setState({email: e.currentTarget.value});
    }

    handleMessage = (e) => {
        this.setState({message: e.currentTarget.value});
    }

    async sendEmail(name, email, message) {
        if (!name || !email || !message) {
            return alert("Please fill all the form")
        }

        await fetch('/contact/send', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Content-Security-Policy': 'font-src *'
            },

            body: JSON.stringify({name: name, email: email, message: message})
        })

        toast.warn("We'll be in touch!");
        this.setState({name: '', email: '', message: ''});
    };

    render() {

        const socialImg = [
            {
                img: "/img/git.png",
                altImg: "git",
                link: 'https://github.com/dvash999'
            }, {
                img: '/img/ld.png',
                altImg: 'linkdin',
                link: 'https://www.linkedin.com/in/dror-dvash/'
            }
        ]

        return (
            <div className="container">
                <Notification/>
                <h1 style={{
                    margin: '20px 0px 60px 0px'
                }}>Contact me :D</h1>
                <form className='form' action="/contact/send" method="POST">
                    <input
                        type="text"
                        name="name"
                        placeholder="Name"
                        value={this.state.name}
                        onChange={this.handleName}
                        className="placeholderDesign"
                        required/>
                    <input
                        type="email"
                        name="email"
                        placeholder="Your Email"
                        value={this.state.email}
                        onChange={this.handleEmail}
                        className="placeholderDesign"
                        required/>
                    <input
                        type="text"
                        name="message"
                        placeholder="Message"
                        value={this.state.message}
                        onChange={this.handleMessage}
                        className="placeholderDesign"
                        required/> {/* <div style={textAreaDesign} contentEditable="true">Messsage</div> */}

                    <button
                        className="btn-contact"
                        type="button"
                        onClick={() => this.sendEmail(this.state.name, this.state.email, this.state.message)}>
                        Send
                    </button>

                </form>

                <div className="social-icon-div">
                    {socialImg.map(({img, altImg, link}) => {
                        return (
                        <img
                        src = {img}
                        alt = {altImg}
                        height = "50"
                        width = "50"
                        style = {{
                            cursor: "pointer"
                                }}
                        onClick = {
                            () => window.open({link})
                        } />
                    )})}
                    {/* <img
                        src="/img/git.jpg"
                        alt="git"
                        height="50"
                        width="50"
                        style={{
                        cursor: "pointer"
                    }}
                        onClick={() => window.open("https://github.com/dvash999")}/>
                    <img
                        src="/img/ld.png"
                        alt="ld"
                        height="50"
                        width="50"
                        style={{
                        cursor: "pointer"
                    }}
                        onClick={() => window.open("https://www.linkedin.com/in/dror-dvash/")}/> */}
                </div>

            </div>
        );
    }
}

export default Contact;
